/**
 * Translation namespace keys (top-level keys)
 */
export type Namespace = keyof Messages;

/**
 * Extract keys for a specific namespace
 */
export type KeysOfNamespace<N extends Namespace> =
  Messages[N] extends Record<string, unknown>
    ? {
        [K in keyof Messages[N]]: Messages[N][K] extends string
          ? `${N}.${K & string}`
          : Messages[N][K] extends Record<string, unknown>
            ? {
                [SK in keyof Messages[N][K]]: Messages[N][K][SK] extends string
                  ? `${N}.${K & string}.${SK & string}`
                  : Messages[N][K][SK] extends Record<string, unknown>
                    ? {
                        [SSK in keyof Messages[N][K][SK]]: `${N}.${K & string}.${SK & string}.${SSK & string}`;
                      }[keyof Messages[N][K][SK]]
                    : never;
              }[keyof Messages[N][K]]
            : never;
      }[keyof Messages[N]]
    : never;

/**
 * Type-safe translation function type
 */
export type TranslateFunction = <K extends TranslationKey>(
  key: K,
  values?: Record<string, string | number>
) => string;

declare module "next-intl" {
  // eslint-disable-next-line @typescript-eslint/no-empty-object-type
  interface IntlMessages extends Messages {}
}
